import React, {useState} from 'react';
import { Add } from '@material-ui/icons';

const Todo = () => {

    const  [inputList, setInputList] = useState("");
    const [items, setItems] = useState([]);

    const itemEvent = (event) => {
        setInputList(event.target.value);
    };

    const listOfItems = () => {
        setItems((oldItems) => {
            return [...oldItems, inputList];
        }) 
    };

    return(
    <div className="container text-center">
    <div className="col-md-6 text-center">
    <div className="card mt-5" style={{width: '18rem'}}>
    <div className="card-body">
    <h5 className="card-title fw-bold">Todo List</h5><hr />
    <input type="text" className="card-text mx-1" placeholder="Add Items" onChange={itemEvent} />
    <a href="#" className="btn btn-outline-success ml-2 rounded" onClick ={listOfItems} > <Add /> </a>
    <ul className="card-text text-start">
        
        {items.map((itemval) => {
            return <li className="list-unstyled"> {items} </li>;
        }
        )}
    </ul>
  </div>
</div>
    </div>
    </div>
    );
};

export default Todo;
