import React, {useState} from 'react';
import { Add, DeleteForever } from '@material-ui/icons';

const Todo = () => {

    const  [inputList, setInputList] = useState("");
    const [items, setItems] = useState([]);

    const itemEvent = (event) => {
        setInputList(event.target.value);
    };

    const listOfItems = () => {
        setItems((oldItems) => {
            return [...oldItems, inputList];
        });
        setInputList('');
    };

    const delItem = (id) =>{
        console.log("Delete");
        setItems((oldItems) => {
            return oldItems.filter((arrElem, index) => {
                return index !==id;
            });
        });

    };

    return(
    <div className="container text-center">
    <div className="col-md-6 offset-md-4 offset-sm-3 text-center">
    <div className="card mt-5 bg-s" style={{width: '18rem'}}>
    <div className="card-body">
    <h5 className="card-title fw-bold">Todo List</h5><hr />
    <input type="text" value={inputList} className="card-text shadow mx-1" placeholder="Add Items" onChange={itemEvent} />
    <a href="#" className="btn btn-outline-success ml-2 rounded add-btn" onClick ={listOfItems} > <Add /> </a>
    <ul className="card-text text-start">
        
        {items.map((itemval) => {
            return <li className="list-unstyled shadow w-75 m-1"><span className="btn del" onClick={delItem}><DeleteForever /> </span> {itemval} </li>;
        }
        )}
    </ul>
  </div>
</div>
    </div>
    </div>
    );
};

export default Todo;
