import React, { useState } from 'react';

const Form = () => {
       let [name, setFullName]=useState({
           fname: "",
           lname: "",
           qaul: "",
           email: "",

       });

       
     const  inputEvent =(event) =>{
            const name= (event.target.name);
            const value= (event.target.value);
       setFullName=((preVal) =>{
                return{
                    ...preVal,
                    [name]: value,
                };
            });
            
       };

    
      const formSubmit = (value) => {
        value.preventDefault();
        alert("Data")
      };

    return (
        <>
           <section className="container">
               <div className="row my-5">
                   <div className="col-md-8 offset-md-2 mx-auto">
                   <h2 className="text-center fw-bold my-3">Registeration Form</h2>
                    <form onSubmit={formSubmit}>

                    <div className="form-group">
                        <label for="exampleInputEmail1">First Name</label>
                        <input type="text" className="form-control" autoComplete="off" id="exampleInputEmail1" ariadescribedby="emailHelp"
                         onChange={inputEvent}
                         name="fname"
                         value={name.fname}
                         />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Last Name</label>
                        <input type="email" className="form-control" id="exampleInputEmail1" ariadescribedby="emailHelp"
                         onChange={inputEvent}
                         name="lname"
                         value={name.lname}
                          />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputPassword1">Qaulification</label>
                        <input type="password" className="form-control" id="exampleInputPassword1"
                         onChange={inputEvent}
                         name="qaul"
                         value={name.qaul}
                         />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" className="form-control" id="exampleInputEmail1" ariadescribedby="emailHelp"
                         onChange={inputEvent}
                         name="email"
                         value={name.email}
                         />
                        </div>
                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                   </div>
               </div>
               <div className="row">
                   <div className="col-md-6 mx-auto text-center">
                        <h3 className="text-success">Hello {name.fname} {name.lname} </h3>
                        <h4 className="text-success">Qaulification: {name.qaul} </h4>
                        <h5 className="text-success">Email: {name.email} </h5>
                   </div>
               </div>
           </section> 
        </>
    )
}

export default Form;
