import React, { useState } from 'react';
import Note from './Note';


const GoogleKeep = () =>{

    const [note, setNote] = useState({ title: "", content: "", });

    const InputEvent= (event) =>{

        // const value = event.target.value;
        // const name = event.target.name;

        const {name, value} = event.target;

        
        setNote((prevData) =>{
            return {
                ...prevData,
                [name]: value,
            }
        })
    }
    return(
     <>
        <div className="container">
         <div className="row mt-5">
             <div className="col-md-4 offset-md-4 shadow-lg py-3">
                 <form action="">
                     <input type="text" name="title" value={note.title} onChange={InputEvent} className="form-control" placeholder="Title" />
                     <textarea name="content" value={note.content} onChange={InputEvent} className="form-control mt-2" id="" cols="10" rows="4" placeholder="Write your note here..."></textarea>
                     <div className="text-center pt-2">
                     <button className="btn btn-outline-success shadow">Add Note</button>
                     </div>
                 </form>
             </div>
         </div>
         <div className="row mt-5">
         <Note />
         </div>
        </div>
     </>
    )};

export default GoogleKeep;
