import React, { useState } from 'react';

const Form = () => {
       const [name, setName]=useState('');
       const [fullName, setFullName]=useState();
       
     const  inputEvent =(event) =>{
            setName(event.target.value);
            
       };

    
      const formSubmit = (value) => {
        value.preventDefault();
        setFullName()
      };

    return (
        <>
           <section className="container">
               <div className="row my-5">
                   <div className="col-md-8 offset-md-2 mx-auto">
                   <h2 className="text-center fw-bold my-3">Registeration Form</h2>
                    <form onSubmit={formSubmit}>

                    <div className="form-group">
                        <label for="exampleInputEmail1">First Name</label>
                        <input type="text" className="form-control" id="exampleInputEmail1" ariadescribedby="emailHelp"
                         onChange={inputEvent}
                         value={name}
                         />
                        </div>
                    {/* <div className="form-group">
                        <label for="exampleInputEmail1">Last Name</label>
                        <input type="email" className="form-control" id="exampleInputEmail1" ariadescribedby="emailHelp" />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputPassword1">Qaulification</label>
                        <input type="password" className="form-control" id="exampleInputPassword1" />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" className="form-control" id="exampleInputEmail1" ariadescribedby="emailHelp" />
                        </div> */}
                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                   </div>
               </div>
               <div className="row">
                   <div className="col-md-6 mx-auto text-center">
                        <h3 className="text-success">Hello {fullName} </h3>
                   </div>
               </div>
           </section> 
        </>
    )
}

export default Form;
