import React from 'react';
import Heading from './Heading';

function App(){
    let curDate = new Date();
curDate = curDate.getHours();
let greeting = "";
const cssStyle = {};

if(curDate >= 1 && curDate < 11)
{
  greeting = "Good Morning";
  cssStyle.color = "green";
}else if(curDate >= 11 && curDate < 19 )
{
  greeting = "Good Afternoon";
  cssStyle.color = "orange";
} else{
  greeting = "Good Evening";
  cssStyle.color = "blue";
}
return(
  <> 
  <div className="sir" >
  <h1> <Heading /> <span style={cssStyle}> {greeting} </span></h1>
  </div>
  </>
);
}
function like(){
  var name = 'Please Like and Share it with your all friends!';
  return name;
};

export {App, like};


function card(props){
  return(
  <>
      <div className="cards">
  <div className="card">
      <img src={props.imgsrc} alt="Image" className="Card Image" />
      <div>
          <span className="Card Category"> {props.sName} </span>
          <h3 Card Title> {props.title} </h3>
          <a href={props.link} target="blank" ></a>
          <button> Watch Now </button>
      </div>
  </div>
</div>
  </>
  );
}

