import React, { useState } from 'react';

const Form = () => {
       let [fullName, setFullName] = useState({
           fname: "",
           lname: "",
           qaul: "",
           email: "",

       });

       
     const  inputEvent =(event) =>{
            const name= (event.target.name);
            const value= (event.target.value);
            // const {name, value} = event.target;


            setFullName=((preValue) => {
                return {
                    ...preValue,
                    [name]:value,
                };
            });
            
       };

    
      const formSubmit = (value) => {
        value.preventDefault();
        alert(`Data Submitted Successfully!
        $
        `)
      };

    return (
        <>
           <section className="container">
               <div className="row my-5">
                   <div className="col-md-8 offset-md-2 mx-auto">
                   <h2 className="text-center fw-bold my-3">Registeration Form</h2>
                    <form onSubmit={formSubmit}>

                    <div className="form-group">
                        <label for="exampleInputEmail1">First Name</label>
                        <input type="text" className="form-control" autoComplete="off" id="exampleInputEmail1" ariadescribedby="emailHelp"
                         placeholder="Enter First Name"
                         onChange={inputEvent}
                         name="fname"
                         value={fullName.fname}
                         />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Last Name</label>
                        <input type="text" className="form-control" autoComplete="off" id="exampleInputEmail1" ariadescribedby="emailHelp"
                         placeholder="Enter Last Name"
                         onChange={inputEvent}
                         name="lname"
                         value={fullName.lname}
                          />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputPassword1">Qaulification</label>
                        <input type="text" className="form-control" autoComplete="off" id="exampleInputPassword1"
                         placeholder="Enter Your Qualification"
                         onChange={inputEvent}
                         name="qaul"
                         value={fullName.qaul}
                         />
                        </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" className="form-control" autoComplete="off" id="exampleInputEmail1" ariadescribedby="emailHelp"
                         placeholder="Enter Your Email"
                         onChange={inputEvent}
                         name="email"
                         value={fullName.email}
                         />
                        </div>
                        <button type="submit" className="btn btn-outline-success mt-3">Submit</button>
                    </form>
                   </div>
               </div>
               <div className="row">
                   <div className="col-md-6 mx-auto text-center">
                        <h3 className="text-success">Full Name: {fullName.fname} {fullName.lname} </h3>
                        <h4 className="text-secondary">Qaulification: {fullName.qaul} </h4>
                        <h5 className="text-success">Email: {fullName.email} </h5>
                   </div>
               </div>
           </section> 
        </>
    )
}

export default Form;
