import React, { useState, useEffect} from 'react';



const RestuCreate = () => {

        const [fullName, setFullName] = useState({
               name: '',
               address: '',
               rating: '',
               email: '',
    
           });
        

         const  create = (() => {
               fetch('http://localhost:3000/restaurant', {
                   method:"post",
                   headers:{
                        'Content-Type':'application.json'
                   }, 
                   body: JSON.stringify(this.state)
                }).then((result) => {
                    result.json().then((resp) =>{
                        console.log(resp)
                    })
                })
               });
           



       const  inputEvent =(event) => {
       const value= (event.target.value);
    //    const name= (event.target.name);
       }


    const formSubmit = (value) => {
    //     value.preventDefault();
    //     alert(`Data Submitted Successfully as Under!
    // -->Name: ${5+5} ${5+5}
    // -->Qualification: ${5+5}
    // -->Email: ${5+5}`)
      };




    return (
        <>
        <section className="container">
            <div className="row">
              <div>
                <h3 className="text-center text-success pt-5">Add New Restaurant</h3>
              </div>
            </div>
        <form className="m-4 p-3 border shadow-lg">
          <div class="row mx-auto g-4 col-12">
            <div class="col-5 offset-1">
              <label for="inputEmail4" className="py-2" >Restaurant Name</label>
              <input type="text" name="name" onChange={inputEvent} class="form-control" placeholder="Enter Restaurant Name" />
            </div>
            <div class="col-5">
            <label for="inputEmail4" className="py-2" >Restaurant Address</label>
              <input type="text" name="address" onChange={inputEvent} class="form-control" placeholder="Enter Address" />
            </div>
            <div class="col-5 offset-1">
            <label for="inputEmail4" className="py-2" >Restaurant Rating</label>
              <input type="number" name="rating" onChange={inputEvent} min="0" max="5" step="0.1" class="form-control" placeholder="Enter Rating from (0-5)" />
            </div>
            <div class="col-5">
            <label for="inputEmail4" className="py-2" >Official Email</label>
              <input type="text" name="n" onChange={inputEvent} class="form-control" placeholder="Enter Email" />
            </div>
            <div class="col-5 mx-auto my-4">
              <input type="submit" onChange={inputEvent} onClick={formSubmit} class="form-control btn btn-outline-success shadow fw-bold" value="Add Restaurant"  />
            </div>
          </div>
        </form>
        </section>
        </>
    )
};

export default RestuCreate;
