import React from 'react';
import { ResponsiveContainer, LineChart, Line, Legend, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';

const pdata = [
    {
        name: 'Jan',
        Sale: 3,
        sValue: 1
    },
    {
        name: 'Feb',
        Sale: 3,
        sValue: 1.5
    },
    {
        name: 'Mar',
        Sale: 4,
        sValue: 3
    },
    {
        name: 'May',
        Sale: 2,
        sValue: 1
    },
    {
        name: 'Jun',
        Sale: 4,
        sValue: 2.5
    },
    {
        name: 'Jul',
        Sale: 5,
        sValue: 2
    },
    {
        name: 'Aug',
        Sale: 4,
        sValue: 2.5
    },
    {
        name: '',
        Sale: 4,
        sValue: 2.5
    },
    {
        name: 'Jun',
        Sale: 4,
        sValue: 2.5
    },
    {
        name: 'Jun',
        Sale: 4,
        sValue: 2.5
    },
    {
        name: 'Jun',
        Sale: 4,
        sValue: 2.5
    },
    
];
const ChartMonthlySales = () => {
    return (
        <>
          <ResponsiveContainer width="100%" aspect={1}>
            <LineChart data={pdata} margin={{right:10,top:10, left:-40}} >
            <Legend />
            <CartesianGrid strokeDasharray="3 3"/>
            <XAxis dataKey="name" tickCount={1} interval={'preserveStartEnd'}/>
            <YAxis tickCount={5} tickFormatter={(v)=>v+"k"} />
             <Line dataKey="fValue" stroke="#30475e" activeDot={{r:5}} />
             <Tooltip contentStyle={{backgroundColor:"#abacad", fontWeight:"400", color:"red"}} />
            </LineChart>
          </ResponsiveContainer>
        </>
    )
}

export default ChartMonthlySales;
